class myWindow(QWidget, Ui_Form):
    def __init__(self):
        super(myWindow, self).__init__()
        self.setupUi(self)
        self.task = None
        self.path = None

        self.radioButton_1.clicked.connect(self.action_radioButton_1)
        self.radioButton_2.clicked.connect(self.action_radioButton_2)
        self.radioButton_3.clicked.connect(self.action_radioButton_3)

        self.pushButton_1.clicked.connect(self.train)
        self.pushButton_2.clicked.connect(self.test)
        self.pushButton_3.clicked.connect(self.choice)
        self.pushButton_4.clicked.connect(self.check)
        self.pushButton_5.clicked.connect(self.clear)
        self.pushButton_6.clicked.connect(self.graphCheck)

    def action_radioButton_1(self):
        self.task = 'pca'
        
    def action_radioButton_2(self):
        self.task = 'bayes'

    def action_radioButton_3(self):
        self.task = 'cnn'

    def train(self):
        self.textBrowser_1.clear()
        info = main.train(self.task)
        for i in range(len(info)):
            self.textBrowser_1.append(info[i])

    def test(self):
        self.textBrowser_1.clear()
        info = main.test(self.task)
        # print(info)
        for i in range(len(info)):
            self.textBrowser_1.append(info[i])
        
    def choice(self):
        file = QFileDialog.getOpenFileName(None, 'choice', '/Users/lhg/work')
        self.path = file[0]
        self.lineEdit_1.setText(file[0])
        self.label_5.setPixmap(QtGui.QPixmap(file[0]))

    def check(self):
        if self.path == None:
            QMessageBox.warning(self, 'WARNING', 'Please choose mode !')
        else:
            self.path = self.lineEdit_1.text()
            self.label_5.setPixmap(QtGui.QPixmap(self.path))
            res = main.singleImage(self.path, self.task, 'choice')
            self.lineEdit_3.setText(str(res))

    def clear(self):
        self.paintBoard.Clear()
        pass

    def graphCheck(self):
        Qt_img = self.paintBoard.GetContentAsQImage()
        PIL_img = ImageQt.fromqimage(Qt_img)
        subImg = PIL_img.resize((28, 28), Image.ANTIALIAS).convert('L')
        res = main.singleImage(subImg, self.task, 'graph')
        self.lineEdit_2.setText(str(res))
        pass

if __name__=='__main__':
    app = QApplication(sys.argv)
    window = myWindow()
    window.show()
    sys.exit(app.exec_())